% complex demodulationfunction[demodmat, phasemat]=compdemod(filemat, targetfreq, bslvec, normflag);if nargin < 4, normflag = 0; endfor index = 1:size(filemat,1);         atgPath = deblank(filemat(index,:));	    [AvgMat,File,Path,FilePath,NTrialAvgVec,StdChanTimeMat,SampRate] = ReadAvgFile(atgPath);         if nargin > 2    AvgMat = bslcorr(AvgMat, bslvec);    end		taxis = 0:1000/SampRate:size(AvgMat,2)*1000/SampRate - 1000/SampRate;	taxis = taxis/1000; % has to be in seconds, so we can use hz later on     	M2 = 50;    	squarecos1 = (cos(pi/2:(pi-pi/2)/M2:pi-(pi-pi/2)/M2)).^2;		squarecosfunction = [squarecos1 ones(1,length(taxis)-length(squarecos1).*2) fliplr(squarecos1)];    	[B, A] = butter(3, 0.008);  % I woudl stick with a butterworth filter with order not much larger than 6 to 8 		for channel = 1:size(AvgMat,1)	        Xsin(channel,:) = AvgMat(channel,:) .* squarecosfunction .* sin(targetfreq .*2 *pi * taxis);        Xcos(channel,:) = AvgMat(channel,:) .* squarecosfunction .* cos(targetfreq .*2 *pi * taxis);	           XsinF(channel,:) = filtfilt(B,A,Xsin(channel,:));         XcosF(channel,:) = filtfilt(B,A,Xcos(channel,:));      end        		demodmat = 2 * sqrt(XsinF .^2 + XcosF .^2);     if ~isempty(bslvec) && normflag == 0        demodmat = bslcorr(demodmat, bslvec);    elseif ~isempty(bslvec) && normflag == 1        demodmat = bslcorr_div(demodmat, bslvec);    end        	phasemat = atan(XsinF ./ XcosF); 	%	[File,Path,FilePath]=SaveAvgFile([atgPath '.amp'],demodmat,NTrialAvgVec,StdChanTimeMat, SampRate);end